import { mapState } from 'vuex'
export default {
    name:"Dialog",
    props:['form','addFlag','dialogVisible'],
    created(){
        //获取商品分类信息
        this.axios.get('/kind').then(response => {
           if(response.status == 200){
               this.kinds = response.data.data; 
           }
       }).catch(error => this.$message(error.message));
    },
    computed:{
        ...mapState('user',{
            sell: 'sell'
        }),
    },
    data(){
        return{
            kinds:[],   //商品类别信息
            fileList:[],  //图片列表
            u:{     //upload提交时附加的data
                commodityId: 0
            },
            action: this.HOST+"/commodityPhoto"
        }
    },
    methods:{  
        //添加商品
        addCommodity(){     
            this.form.shopId = this.sell.id;
            if(this.form.bargain==false){      //0不许议价，1允许议价
                this.form.bargain=0;
            }else if(this.form.bargain==true){
                 this.form.bargain=1;
            }
            this.axios.post('/commodity',this.form).then(response=>{
                if(response.data.code==0){
                    this.u.commodityId = response.data.data;
                    this.$refs.upload.submit();     //手动提交upload
                    this.dialogHide();
                    // this.$router.go(0);
                    this.$message("添加成功");
                }else{
                    this.$message("添加失败！")
                }
            }).catch(error => alert(error));
            // this.dialogVisible = false;
        },
        
        //修改商品信息
        updateCommodity(){
            this.$confirm('确认修改？')
            .then(() => {
                if(this.form.bargain==false){      //0不许议价，1允许议价
                    this.form.bargain=0;
                }else if(this.form.bargain==true){
                        this.form.bargain=1;
                }
               this.axios.post("/commodity/update",this.form).then(response => {
                   if(response.data.code==0){
                       this.$emit('update',this.form);
                       this.dialogHide();
                       this.$router.go(0);
                       this.$message({
                           message:"修改成功",
                           type:"success"
                       })
                   }
               })
            })
            .catch(() => {});
        },

        //通知父组件关闭dialog
        dialogHide(){
            this.$emit("dialogHide");
        },

        //上传图片
        // handleRemove(file, fileList) {
        //     console.log(file, fileList);
        // },
        handleRemove(file, fileList) {
    // 调用后端接口删除旧图片
    this.axios.post('/commodityPhoto/delete', { id: file.id })
        .then(response => {
            const index = fileList.indexOf(file);
            fileList.splice(index, 1);  // 从 fileList 中删除被删除的文件
            this.$message.success('删除成功');
        })
        .catch(error => {
            console.error('删除失败', error);
            this.$message.error('删除失败');
        });
},

        handlePreview(file) {
            console.log(file);
        },

        handleClose() {
            this.$confirm('确认关闭？')
            .then(() => {
                this.dialogHide();
            })
            .catch(() => {});
        },
    }
}
